package cmd

func NewShortID(isGRSC bool, id byte) byte {
	if id > 0x80 {
		panic("id should be less than 128")
	}
	if isGRSC {
		return id
	}
	return 0x80 | id
}

func ParseShortID(shortID byte) (isGRSC bool, id byte) {
	if shortID >= 0x80 {
		return false, shortID & 0x7f
	}
	return true, shortID
}

